var daily = [];
var update = false;
var retries = global.get("open_weather_retries") || 0;

if (msg.statusCode && msg.statusCode === 200 && msg.payload.daily) {
  global.set("open_weather_retries", 0);
  if (msg.payload.daily) {
    msg.payload.daily.forEach((day, i) => {
      daily[i] = {
        rain: day.rain || 0,
        wind: day.wind_speed || 0,
        max_temp: day.temp.max || 0,
      };
    });
    update = true;
  }

  node.send([null, { payload: daily }], null);

  if (daily[0].rain >= 10.0) {
    // Turn off irrigation when it rains over 10 mm.
    node.send([{ payload: "off", weather: daily[0] }, null, null]);
  } else if (daily[0].rain >= 5.0) {
    // Decrease irrigation by 50% when it rains over 5 mm
    node.send([{ payload: "reduced", weather: daily[0] }, null, null]);
  } else if (daily[0].wind_speed >= 50.0) {
    // Delay irrigation if wind speeds over 50 km/hr are forecast.
    node.send([{ payload: "delay", weather: daily[0] }, null, null]);
  } else if (daily[0].max_temp >= 35.0) {
    // Increase irrigation by 20% if forecast temperature is over 35 degrees.
    node.send([{ payload: "increase", weather: daily[0] }, null, null]);
  } else {
    node.send([{ payload: "normal", weather: daily[0] }, null, null]);
  }

  if (update || msg.payload.update == "status") {
    node.status({
      fill: "blue",
      shape: "ring",
      text: `${daily[0].rain}mm, ${daily[0].wind}km/h, ${daily[0].max_temp}â„ƒ`,
    });
    setTimeout(() => {
      node.status({
        fill: "grey",
        shape: "dot",
        text: `${daily[0].rain}mm, ${daily[0].wind}km/h, ${daily[0].max_temp}â„ƒ`,
      });
    }, 1500);
  }
} else {
  if (retries <= 3) {
      retries = retries+1;
     global.set("open_weather_retries", retries)
    node.warn(`Openweather API ERROR, retry in ${retries}min`);

    setTimeout(() => {
      node.send([null, null, { payload: "retry" }]);
    }, 60000 * retries);
  } else {
    node.warn(`Openweather API FAILED, triggering outputs`);
    node.send([{ payload: "normal" }, null, null]);
    global.set("open_weather_retries", 0);
  }
}

/*********************************************************************
  //    User Configurations.
**********************************************************************/

//
//  this is an array channel times
//
var times = [5];

/*********************************************************************
  //    End User Configurations.
**********************************************************************/

let output = [];
let mode;

times.forEach((times, i) => {
  if (msg.payload == "off") {
    // Turn off irrigation when it rains over 10 mm.
    output[i] = 0;
    mode = "Skip: rains over 10 mm";
  } else if (msg.payload == "reduced") {
    // Decrease irrigation by 50% when it rains over 5 mm
    output[i] = times / 2;
     mode = "Reduced: rains over 5 mm";
  } else if (msg.payload == "delay") {
    // Delay irrigation if wind speeds over 50 km/hr are forecast.
    output[i] = -1;
    mode = "Delayed: wind speeds over 50 km/hr, will try again in an hour";
    setTimeout(() => {
      node.send([null, { payload: "retry" }, null]);
    }, 60000 * 60);
  } else if (msg.payload == "increase") {
    // Increase irrigation by 20% if forecast temperature is over 35 degrees.
    output[i] = times + times * 0.2;
     mode = "Increased: over 35 degrees";
  } else {
    output[i] = times;
    mode = "Normal watering.";
  }

  triggerOutput(i, output[i]);
  node.send([  null, null, { payload: `${mode}\nToday: ${msg.weather.max_temp}â„ƒ, ${msg.weather.rain}mm,  ${msg.weather.wind}km/h` }]);
});

function triggerOutput(channel, period) {
  if (period > 0) {
    node.send([
      { topic: `waterbird/set/ch${channel + 1}`, payload: "on" },
      null, null
    ]);
    setTimeout(() => {
      node.send([
        { topic: `waterbird/set/ch${channel + 1}`, payload: "off" },
        null, null
      ]);
    }, period * 60000);
  } else {
    node.send([
      { topic: `waterbird/set/ch${channel + 1}`, payload: "off" },
      null, null
    ]);
  }
}

let str = "";
if (output[0] === 0) {
  str = "Off";
} else if (output[0] === -1) {
  str = "Delayed";
} else {
  output.forEach((x, i) => {
    str = str + `${x}m, `;
  });
}
node.status({
  fill: "blue",
  shape: "ring",
  text: str,
});
setTimeout(() => {
  node.status({
    fill: "grey",
    shape: "dot",
    text: "",
  });
}, 10000);

var retries = global.get("waterbird_ping_retries") || 0;
var interval = global.get("waterbird_ping_interval") || null;

if (retries === 0 || msg.payload === "reset") {
  retries = 1;
  clearInterval(interval);
  node.status({
    fill: "blue",
    shape: "dot",
    text: `RESET`,
  });

  interval = setInterval(() => {
    var retries = global.get("waterbird_ping_retries") || 0;
    if (retries < 3) {
      retries = retries + 1;
    } else {
      node.status({
        fill: "red",
        shape: "dot",
        text: `OFFLINE`,
      });
    }
    global.set("waterbird_ping_retries", retries);
    node.send({
      topic: `waterbird/pingreq`,
      payload: "",
    });
  }, 30000);
} else if (msg.topic === "waterbird/pingresp" && msg.payload) {
  retries = 1;
  node.status({
    fill: "green",
    shape: "dot",
    text: `Uptime: ${msToTime(msg.payload)}`,
  });
}

function msToTime(s) {
  function pad(n, z) {
    z = z || 2;
    return ("00" + n).slice(-z);
  }

  var ms = s % 1000;
  s = (s - ms) / 1000;
  var secs = s % 60;
  s = (s - secs) / 60;
  var mins = s % 60;
  s = (s - mins) / 60;
  var hrs = s % 24;
  var days = (s - hrs) / 24;

  return `${days}d, ${pad(hrs)}h, ${pad(mins)}m, ${pad(secs)}s.`;
}

global.set("waterbird_ping_retries", retries);
global.set("waterbird_ping_interval", interval);
